import './App.css';
import { createElement } from 'react';
import Class from './newClass';

var reg = []
function App() {

  function AddDetails() {
    var name = document.querySelector('#name').value
    var email = document.querySelector('#email').value
    var male = document.querySelector('#male').checked
    var female = document.querySelector('#female').checked
    var other = document.querySelector('#Other').checked
    var js = document.querySelector('#js').checked
    var html = document.querySelector('#html').checked
    var css = document.querySelector('#css').checked
    var gender;
    var skills = [];
    if(male)
    {
      gender = 'Male';
    }
    else if(female){
      gender = 'Female'
    }
    else{
      gender = 'Other'
    }

    if(js)
    {
      skills.push('Js')
    }
    if(html){
      skills.push('HTML')
    }
    if(css){
      skills.push('CSS')
    }
    if(!html && !css && !js)
    {
      skills.push('None')
    }

    const deets = {name:name, email:email, gender:gender, skills:skills}
    console.log(deets)
  }

  return (
    <div className="App">
      <div className='container'>
        <h2>Student Registration Form</h2>

        <div id='MainBody'>
          <div id='register'>
            <form id='form'>
            <div className='details'><p>Name:</p><input type='text' id='name'></input></div>
            <div className='details'><p>Email:</p><input type='text' id='email'></input></div>
            <div className='details'><p>Gender:</p>
              <input type='radio' id='male'></input>
              <label htmlFor='male'>Male</label>
              <input type='radio' id='female'></input>
              <label htmlFor='female'>Female</label>
              <input type='radio' id='Other'></input>
              <label htmlFor='Other'>Other</label>
            </div>
            <div className='details'><p>Skills:</p>
              <input type='checkbox' id='js'></input>
              <label htmlFor='js'>JS</label>
              <input type='checkbox' id='html'></input>
              <label htmlFor='html'>HTML</label>
              <input type='checkbox' id='css'></input>
              <label htmlFor='css'>CSS</label>
            </div>
            </form>
            <button onClick={AddDetails}>Submit</button>
          </div>
          <div id='registered'>
          {reg.map(function(reg, i){
              <div className='registeredDetails'>
                Hello
              </div>
          })}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
